mapper.update:
update character_level_exp
set is_delete = 1
where id = #{id}

mapper.select:
select count(1) from character_level_exp
is_delete = 0
and level = #{level}

select *
from character_level_exp

    is_delete = 0
    
        and level = #{level}
    

order by level asc

    limit ${(pageParam.pageIndex - 1) * pageParam.pageSize}, ${pageParam.pageSize}
