mapper.select:
select count(1)
from map_mob

    is_delete = 0
    
        and map_id = #{mapId}
    
    
        and faction = #{faction}
    
    
        and mob_class = #{mobClass}
    
    
        and mob_type = #{mobType}
    
    
        and level >= #{levelStart}
    
    
        and level <= #{levelEnd}
    
    
        and name like concat('%', #{name}, '%')

select *
from map_mob

    is_delete = 0
    
        and map_id = #{mapId}
    
    
        and faction = #{faction}
    
    
        and mob_class = #{mobClass}
    
    
        and mob_type = #{mobType}
    
    
        and level >= #{levelStart}
    
    
        and level <= #{levelEnd}
    
    
        and name like concat('%', #{name}, '%')
    


    limit ${(pageParam.pageIndex - 1) * pageParam.pageSize}, ${pageParam.pageSize}
